<script>
  import {mapState, mapMutations} from 'vuex'
  export default {
    name: 'page-header',
    data() {
      return {
        menus: [
          {name: 'home', path: '/home', active: true},
          {name: 'blog', path: '/blog', active: false},
          {name: 'example', path: '/example', active: false},
          {name: 'components', path: '/components', active: false},
          {name: 'music', path: '/music', active: false},
          {name: 'about', path: '/about', active: false}
        ],
        isToggle: false
      }
    },
    computed: {
      ...mapState('app', {
        isScrollUp: 'isScrollUp',
        isPageScroll: 'isPageScroll',
        isSideMenuActive: 'isSideMenuActive'
      })
    },
    methods: {
      ...mapMutations('app', {
          menuToggle: 'SIDE_MENU_ACTIVE'
      }),
      shadowToggle(event) {
        event = event || window.event;
        let target = event.target || event.srcElement;
        if(target.nodeName.toLowerCase() === 'section') {
          this.menuToggle();
        }
        // 阻止事件冒泡
        event.stopPropagation();
      },
      getPathLength() {
        let path = document.getElementById('line');
        //获取路径总长度
        let length = path.getTotalLength();
        // console.log('length:===>' + length);
      }
    }
  }
</script>

